import 'package:flutter/material.dart';import 'package:schoolworkspro_app/api/api_response.dart';import 'package:schoolworkspro_app/api/repositories/achievements_repo.dart';import '../../response/my_achievements_response.dart';class AchievementViewModel extends ChangeNotifier {  ApiResponse _myAchievementApiResponse = ApiResponse.initial("Empty Data");  ApiResponse get myAchievementApiResponse => _myAchievementApiResponse;  MyAchievementResponse _myAchievement = MyAchievementResponse();  MyAchievementResponse get myAchievement => _myAchievement;  Future<void> fetchMyAchievements() async {    _myAchievementApiResponse = ApiResponse.initial("Loading");    notifyListeners();    try {      MyAchievementResponse res = await AchievementsRepository().getMyAchievements();      _myAchievement = res;      print("check"+res.toString());      _myAchievementApiResponse = ApiResponse.completed(res.success.toString());      notifyListeners();    } catch (e) {      print("VM CATCH ERR :: " + e.toString());      _myAchievementApiResponse = ApiResponse.error(e.toString());    }    notifyListeners();  }}