import 'dart:convert';import 'package:flutter/material.dart';import 'package:qr_flutter/qr_flutter.dart';import 'package:schoolworkspro_app/Screens/lecturer/events/event_applicants_screen.dart';class EventQrScreen extends StatelessWidget {  String id;  EventQrScreen({Key? key, required this.id}) : super(key: key);  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(          iconTheme: IconThemeData(color: Colors.black),          title: const Text("Events",              style: TextStyle(color: Colors.black)),          backgroundColor: Colors.white),      body: SafeArea(          child: Padding(        padding: const EdgeInsets.symmetric(horizontal: 10, vertical: 15),        child: Column(          crossAxisAlignment: CrossAxisAlignment.end,          children: [            InkWell(                onTap: () {                  Navigator.push(                      context,                      MaterialPageRoute(                          builder: (context) => EventApplicantScreen(eventId: id,)));                },                child: Container(                    height: 30,                    width: 140,                    decoration: BoxDecoration(                        color: Color(0XFFdc3545),                        borderRadius: BorderRadius.circular(4)),                    child: Center(                      child: Text(                        "View Applicants",                        style: TextStyle(                            color: Colors.white,                            fontWeight: FontWeight.bold),                      ),                    ))),            SizedBox(              height: 20,            ),            Builder(              builder: (context) {                final data = {"id":id};                final jsonConvert = json.encode(data);                return Center(                  child: QrImageView(                    data: jsonConvert.toString(),                    version: QrVersions.auto,                    size: 350.0,                  ),                );              }            ),          ],        ),      )),    );  }}