import 'package:flutter/material.dart';import 'package:schoolworkspro_app/api/api_response.dart';import 'package:schoolworkspro_app/response/principal/terminal_exam_response.dart';import 'package:schoolworkspro_app/services/event_service.dart';import '../../../api/repositories/exam_repo.dart';class LecturerEventViewModel extends ChangeNotifier {  ApiResponse _terminalApiResponse = ApiResponse.initial("Empty Data");  ApiResponse get terminalApiResponse => _terminalApiResponse;  TerminalExamResponse _data= TerminalExamResponse();  TerminalExamResponse get data=> _data;  Future<void> fetchTerminal() async {    _terminalApiResponse = ApiResponse.initial("Loading");    notifyListeners();    try {      TerminalExamResponse res = await EventService().getTerminal();      if (res.success == true) {        _data= res;        _terminalApiResponse =            ApiResponse.completed(res.success.toString());        notifyListeners();      } else {        _terminalApiResponse = ApiResponse.error(res.success.toString());      }    } catch (e) {      print("VM CATCH ERR :: " + e.toString());      _terminalApiResponse = ApiResponse.error(e.toString());    }    notifyListeners();  }}