import 'package:flutter/material.dart';import 'package:schoolworkspro_app/api/api_response.dart';import 'package:schoolworkspro_app/api/repositories/lecturer/check_submission_repository.dart';import 'package:schoolworkspro_app/response/lecturer/check_submission_response.dart';class CheckSubmissionViewModel extends ChangeNotifier {  ApiResponse _getStudentTaskApiResponse = ApiResponse.initial("Empty Data");  ApiResponse get studentMarkingApiResponse => _getStudentTaskApiResponse;  CheckSubmissionResponse _data = CheckSubmissionResponse();  CheckSubmissionResponse get data => _data;  Future<void> fetchStudentTask(taskName, batch) async {    _getStudentTaskApiResponse = ApiResponse.initial("Loading");    notifyListeners();    try {      CheckSubmissionResponse res =      await CheckSubmissionRepository().getStudentTasks(taskName, batch);      if (res.success == true) {        _data = res;        _getStudentTaskApiResponse =            ApiResponse.completed(res.success.toString());        notifyListeners();      } else {        _getStudentTaskApiResponse = ApiResponse.error(res.success.toString());      }    } catch (e) {      print("VM CATCH ERR :: " + e.toString());      _getStudentTaskApiResponse = ApiResponse.error(e.toString());    }    notifyListeners();  }}