import 'package:flutter/material.dart';import 'package:page_transition/page_transition.dart';import '../../../my_learning/additional_resources/additional_resource_screen.dart';import '../components/more_component/add_homework_task_screen.dart';import '../components/more_component/add_teaching_material_screen.dart';import '../components/more_component/view_homework_screen.dart';class LessonMoreScreen extends StatefulWidget {  final lessonSlug;  final moduleSlug;  final moduelTitle;  const LessonMoreScreen({Key? key, required this.lessonSlug, required this.moduleSlug, this.moduelTitle}) : super(key: key);  @override  _LessonMoreScreenState createState() => _LessonMoreScreenState();}class _LessonMoreScreenState extends State<LessonMoreScreen> {  @override  Widget build(BuildContext context) {    return ListView(      children: [        const SizedBox(          height: 15,        ),        Card(          child: ListTile(            onTap: () {              Navigator.push(                  context,                  MaterialPageRoute(                    builder: (context) => AdditionalResourcesScreen(lessonSlug: widget.lessonSlug),                  ));            },            title: const Text("Additional Resources"),          ),        ),        Card(          child: ListTile(            onTap: () {              Navigator.push(                  context,                  PageTransition(                      child: HomeWorkTaskScreen(                        moduleSlug: widget.moduleSlug,                        moduleTitle: widget.moduelTitle,                      ),                      type: PageTransitionType.leftToRight));            },            title: const Text("Digital Diary"),          ),        ),        Card(          child: ListTile(            onTap: () {              Navigator.push(                  context,                  MaterialPageRoute(                      builder: (context) => AddTeachingMaterialScreen(lessonSlug: widget.lessonSlug)                  ));            },            title: const Text("Teaching Material"),          ),        ),      ],    );  }}