import 'package:flutter/material.dart';import 'package:schoolworkspro_app/api/api_response.dart';import '../../../api/repositories/student_assessement_repo.dart';import '../../../response/assessment_response.dart';import '../../../response/assessment_submission_response.dart';import '../../../services/assessment_service.dart';class ActivityViewModel extends ChangeNotifier {  ApiResponse _checkSubmissionApiResponse = ApiResponse.initial("Empty Data");  ApiResponse get checkSubmissionApiResponse => _checkSubmissionApiResponse;  AssessmentSubmissionResponse _checkSubmission = AssessmentSubmissionResponse();  AssessmentSubmissionResponse get checkSubmission => _checkSubmission;  Future<void> fetchCheckSubmission(String lessonSlug) async {    _checkSubmissionApiResponse = ApiResponse.initial("Loading");    notifyListeners();    try {      AssessmentSubmissionResponse res = await StudentAssessmentRepository().getCheckSubmission(lessonSlug);        _checkSubmission = res;        print("check"+res.toString());        _checkSubmissionApiResponse = ApiResponse.completed(res.success.toString());        notifyListeners();    } catch (e) {      print("VM CATCH ERR :: " + e.toString());      _checkSubmissionApiResponse = ApiResponse.error(e.toString());    }    notifyListeners();  }  ApiResponse _myAssessmentApiResponse = ApiResponse.initial("Empty Data");  ApiResponse get myAssessmentApiResponse => _myAssessmentApiResponse;  Assessmentresponse _myAssessment = Assessmentresponse();  Assessmentresponse get myAssessment => _myAssessment;  Future<void> fetchMyAssessment(String moduleSlug) async {    _myAssessmentApiResponse = ApiResponse.initial("Loading");    notifyListeners();    try {      Assessmentresponse res = await Assessmentservice().getAssessment(moduleSlug);      if (res.success == true) {        _myAssessment = res;        _myAssessmentApiResponse = ApiResponse.completed(res.success.toString());        notifyListeners();      } else {        _myAssessmentApiResponse = ApiResponse.error(res.success.toString());      }    } catch (e) {      print("VM CATCH ERR :: " + e.toString());      _myAssessmentApiResponse = ApiResponse.error(e.toString());    }    notifyListeners();  }}