import 'package:flutter/material.dart';import 'package:schoolworkspro_app/api/api_response.dart';import '../../../api/repositories/teaching_material_repo.dart';import '../../../response/teaching_material_response.dart';class TeachingMaterialViewModel extends ChangeNotifier {  ApiResponse _teachingMaterialApiResponse = ApiResponse.initial("Empty Data");  ApiResponse get teachingMaterialApiResponse => _teachingMaterialApiResponse;  TeachingMaterialResponse _materials = TeachingMaterialResponse();  TeachingMaterialResponse get materials => _materials;  Future<void> fetchTeachingMaterials(String titleSlug) async {    _teachingMaterialApiResponse = ApiResponse.initial("Loading");    notifyListeners();    try {      TeachingMaterialResponse res =      await TeachingMaterialRepository().getTeachingMaterials(titleSlug);      if (res.success == true) {        _materials = res;        _teachingMaterialApiResponse =            ApiResponse.completed(res.success.toString());        notifyListeners();      } else {        _teachingMaterialApiResponse =            ApiResponse.error(res.success.toString());      }    } catch (e) {      print("VM CATCH ERR :: " + e.toString());      _teachingMaterialApiResponse = ApiResponse.error(e.toString());    }    notifyListeners();  }}