import 'package:flutter/material.dart';import '../../../../../api/api_response.dart';import '../../../../../response/parents/getparentticketdetail_response.dart';import '../../../../../services/parents/getparentrequest_service.dart';class ParentRequestViewModel extends ChangeNotifier {  ApiResponse _parentRequestApiResponse = ApiResponse.initial("Empty Data");  ApiResponse get parentRequestApiResponse => _parentRequestApiResponse;  Getparentticketdetailresponse _parentRequest = Getparentticketdetailresponse();  Getparentticketdetailresponse get parentRequest => _parentRequest;  Future<void> fetchRequestDetails(String id) async {    _parentRequestApiResponse = ApiResponse.initial("Loading");    notifyListeners();    try {      Getparentticketdetailresponse res = await Getparentrequestservice().getrequestdetail(id);      if (res.success == true) {        _parentRequest = res;        _parentRequestApiResponse = ApiResponse.completed(res.success.toString());        notifyListeners();      } else {        _parentRequestApiResponse = ApiResponse.error(res.success.toString());      }    } catch (e) {      print("VM CATCH ERR :: " + e.toString());      _parentRequestApiResponse = ApiResponse.error(e.toString());    }    notifyListeners();  }}