import 'dart:convert';import 'package:schoolworkspro_app/api/api.dart';import 'package:schoolworkspro_app/response/refrence_links_response.dart';import '../../response/additional_books_links_response.dart';import '../../response/youtube_video_additional_resources_response.dart';class AdditionalResourceRepository {  API api = API();  Future<YoutubeAdditinalResourcesResponse> getYoutubeLinks(String titleSlug) async {    API api = new API();    dynamic response;    YoutubeAdditinalResourcesResponse res;    try {      response = await api.getWithToken('/lessons/links/youtube/$titleSlug');      res = YoutubeAdditinalResourcesResponse.fromJson(response);    } catch (e) {      res = YoutubeAdditinalResourcesResponse.fromJson(response);    }    return res;  }  Future<RefreshLinksResourcesResponse> getRefreshLinks(String titleSlug) async {    API api = new API();    dynamic response;    RefreshLinksResourcesResponse res;    try {      response = await api.getWithToken('/lessons/links/google/$titleSlug');      res = RefreshLinksResourcesResponse.fromJson(response);    } catch (e) {      res = RefreshLinksResourcesResponse.fromJson(response);    }    return res;  }  Future<BooksLinksResponse> getBooksLinks(String titleSlug) async {    API api = new API();    dynamic response;    BooksLinksResponse res;    try {      response = await api.getWithToken('/lessons/links/books/$titleSlug');      res = BooksLinksResponse.fromJson(response);    } catch (e) {      res = BooksLinksResponse.fromJson(response);    }    return res;  }}