import 'dart:convert';import 'package:schoolworkspro_app/api/api.dart';import 'package:schoolworkspro_app/response/lecturer/advisor_assigned_response.dart';import 'package:schoolworkspro_app/response/lecturer/getgradesheading_response.dart';class AdvisorAssignedRepository {  API api = API();  Future<AdivsorAssignedLecturerResponse> getAssigned() async {    API api = new API();    dynamic response;    AdivsorAssignedLecturerResponse res;    try {      response =      await api.getWithToken('/advisors/get-assigned');      print(response.toString());      res = AdivsorAssignedLecturerResponse.fromJson(response);    } catch (e) {      print("FROM REPOOO:::${e.toString()}");      res = AdivsorAssignedLecturerResponse.fromJson(response);    }    return res;  }}