import 'dart:convert';import 'package:schoolworkspro_app/api/api.dart';import 'package:schoolworkspro_app/response/lecturer/check_submission_response.dart';import 'package:schoolworkspro_app/response/lecturer/student_homework_marking_reponse.dart';class CheckSubmissionRepository{  API api = API();  Future<CheckSubmissionResponse> getStudentTasks(String taskName ,String batch) async {    API api = new API();    dynamic response;    CheckSubmissionResponse res;    try {      response = await api.getWithToken('/task/getTask/$taskName/$batch');      res = CheckSubmissionResponse.fromJson(response);    } catch (e) {      res = CheckSubmissionResponse.fromJson(response);    }    return res;  }  Future<StudentHomeworkMarkingResponse> postStudentHomeworkMarking(data, taskName) async {    API api = new API();    // Box box = HiveUtils.box;    dynamic response;    StudentHomeworkMarkingResponse res;    try {      response = await api.putDataWithToken(jsonEncode(data), '/task/checkTask/$taskName');      res = StudentHomeworkMarkingResponse.fromJson(response);    } catch (e) {      res = StudentHomeworkMarkingResponse.fromJson(response);    }    return res;  }}