import 'dart:convert';import 'package:schoolworkspro_app/api/api.dart';import 'package:schoolworkspro_app/response/common_response.dart';class EditMarksRepository{  API api = API();  Future<Commonresponse> editMarks(id,data) async {    API api = new API();    dynamic response;    Commonresponse res;    try {      response = await api.putDataWithToken(jsonEncode(data), '/marks/$id');      res = Commonresponse.fromJson(response);    } catch (e) {      res = Commonresponse.fromJson(response);    }    return res;  }}