import 'dart:convert';import 'package:schoolworkspro_app/api/api.dart';import 'package:schoolworkspro_app/response/parents/get_children_module.dart';import '../../../response/parents/offline_task_response.dart';class ChildrenHomeworkRepository {  Future<ChildrenModuleResponse> getModule(String institution, String username) async {    API api = API();    dynamic response;    ChildrenModuleResponse res;    try {      response = await api.postDataWithToken(jsonEncode({"institution": institution}), '/task/student-modules/$username', );      res = ChildrenModuleResponse.fromJson(response);    } catch (e) {      res = ChildrenModuleResponse.fromJson(response);    }    return res;  }  Future<ChildrenOfflineTaskResponse> getOfflineTask(String institution, String moduleSlug, String username) async {    API api = API();    dynamic response;    ChildrenOfflineTaskResponse res;    Map<String,dynamic> data = {"institution": institution,      "moduleSlug": moduleSlug};    print("DATAAA${data}");    try {      response = await api.postDataWithToken(jsonEncode(data), '/task/all-tasks/$username', );      res = ChildrenOfflineTaskResponse.fromJson(response);    } catch (e) {      res = ChildrenOfflineTaskResponse.fromJson(response);    }    return res;  }}