import 'dart:convert';import 'package:schoolworkspro_app/api/api.dart';import 'package:schoolworkspro_app/request/postadvisor_request.dart';import 'package:schoolworkspro_app/request/referral_details_request.dart';import 'package:schoolworkspro_app/response/principal/assign_to_response.dart';import 'package:schoolworkspro_app/response/principal/assigned_advisor_response.dart';import 'package:schoolworkspro_app/response/principal/getadvisors_response.dart';import 'package:schoolworkspro_app/response/principal/postadvisor_response.dart';import 'package:schoolworkspro_app/response/principal/referral_details_response.dart';class AssignedToStaffRepository {  Future<AssignToResponse> getStaff() async {    API api = API();    dynamic response;    AssignToResponse res;    try {      response = await api.getWithToken('/advisors/get-staff');      res = AssignToResponse.fromJson(response);    } catch (e) {      res = AssignToResponse.fromJson(response);    }    return res;  }  Future<AssignedAdvisorResponse> assignedTo(id, data) async {    API api = API();    dynamic response;    AssignedAdvisorResponse res;    try {      response = await api.putDataWithToken(jsonEncode(data), '/advisors/assign-staff/$id');      res = AssignedAdvisorResponse.fromJson(response);    } catch (e) {      res = AssignedAdvisorResponse.fromJson(response);    }    return res;  }}