import 'dart:convert';import 'package:schoolworkspro_app/api/api.dart';import 'package:schoolworkspro_app/response/lecturer/get_homework_response.dart';import 'package:schoolworkspro_app/response/student_homework_info.dart';import 'package:shared_preferences/shared_preferences.dart';import 'package:http/http.dart' as http;import '../../response/digital_diary_response.dart';class StudentHomeworkRepository{  API api = API();  Future<GetHomeworkResponse> getStudentHomework(String moduleSlug ,String batch) async {    API api = new API();    dynamic response;    GetHomeworkResponse res;    try {      response = await api.getWithToken('/task/get-task/$batch/$moduleSlug');      print(response);      res = GetHomeworkResponse.fromJson(response);    } catch (e) {      res = GetHomeworkResponse.fromJson(response);    }    return res;  }  Future<StrudentHomeworkInfoResponse> getStudentInfo(String taskSlug) async {    API api = new API();    dynamic response;    StrudentHomeworkInfoResponse res;    try {      response = await api.getWithToken('/task/check-task/$taskSlug');      print(response);      res = StrudentHomeworkInfoResponse.fromJson(response);    } catch (e) {      res = StrudentHomeworkInfoResponse.fromJson(response);    }    return res;  }  Future<DigitalDiaryResponse> getDigitalDiary(String time) async {    API api = new API();    dynamic response;    DigitalDiaryResponse res;    try {      response = await api.getWithToken('/task/getDiaries?date=$time');      print(response);      res = DigitalDiaryResponse.fromJson(response);    } catch (e) {      res = DigitalDiaryResponse.fromJson(response);    }    return res;  }}