// To parse this JSON data, do////     final booksLinksResponse = booksLinksResponseFromJson(jsonString);import 'dart:convert';BooksLinksResponse booksLinksResponseFromJson(String str) => BooksLinksResponse.fromJson(json.decode(str));String booksLinksResponseToJson(BooksLinksResponse data) => json.encode(data.toJson());class BooksLinksResponse {  BooksLinksResponse({    this.success,    this.books,  });  bool? success;  List<Book>? books;  factory BooksLinksResponse.fromJson(Map<String, dynamic> json) => BooksLinksResponse(    success: json["success"],    books: List<Book>.from(json["books"].map((x) => Book.fromJson(x))),  );  Map<String, dynamic> toJson() => {    "success": success,    "books": List<dynamic>.from(books!.map((x) => x.toJson())),  };}class Book {  Book({    this.institution,    this.id,    this.size,    this.createdAt,    this.slug,    this.file,    this.category,    this.name,  });  List<dynamic>? institution;  String? id;  String? size;  DateTime? createdAt;  String? slug;  String? file;  String? category;  String? name;  factory Book.fromJson(Map<String, dynamic> json) => Book(    institution: List<dynamic>.from(json["institution"].map((x) => x)),    id: json["_id"],    size: json["size"],    createdAt: DateTime.parse(json["createdAt"]),    slug: json["slug"],    file: json["file"],    category: json["category"],    name: json["name"],  );  Map<String, dynamic> toJson() => {    "institution": List<dynamic>.from(institution!.map((x) => x)),    "_id": id,    "size": size,    "createdAt": createdAt?.toIso8601String(),    "slug": slug,    "file": file,    "category": category,    "name": name,  };}