    "dueDate": dueDate?.toIso8601String(),    "taskname": taskname,    "content": content,    "moduleSlug": moduleSlug,    "institution": institution,    "students": List<dynamic>.from(students!.map((x) => x.toJson())),    "assignedDate": assignedDate?.toIso8601String(),    "taskSlug": taskSlug,  };}class Student {  Student({    this.submitted,    this.id,    this.username,    this.feedback,  });  bool? submitted;  String? id;  String? username;  String? feedback;  factory Student.fromJson(Map<String, dynamic> json) => Student(    submitted: json["submitted"],    id: json["_id"],    username: json["username"],    feedback: json["feedback"],  );  Map<String, dynamic> toJson() => {    "submitted": submitted,    "_id": id,    "username": username,    "feedback": feedback,  };}